mapper.select:
SELECT a.userId, name, itemName, i.itemId, phone, address, myPrice, credit, picture, deadline
FROM ACCOUNT a, item i, AuctionItem u
WHERE a.userId = u.userId and i.itemId = u. itemId and a.USERID = #{userId} and i.itemId = #{itemId}

mapper.update:
  	UPDATE AUCTIONEDITEM
	SET DESTINATION = #{destination}
	WHERE USERID = #{userId}
	AND ITEMID = #{itemId}
